const now = new Date();
const dayOfWeek = now.getDay(); // 0 = Sonntag, 1 = Montag, ...

const dayMapping = {
  1: { post_type: 'authority_builder', image_type: 'karikatur', weekday: 'Montag' },
  2: { post_type: 'client_win', image_type: 'karikatur', weekday: 'Dienstag' },
  3: { post_type: 'controversy', image_type: 'karikatur', weekday: 'Mittwoch' },
  4: { post_type: 'mistake', image_type: 'ugc', weekday: 'Donnerstag' },
  5: { post_type: 'audit', image_type: 'karikatur', weekday: 'Freitag' },
  6: { post_type: 'how_to', image_type: 'ugc', weekday: 'Samstag' }
};

const todayConfig = dayMapping[dayOfWeek];

if (!todayConfig) {
  throw new Error('Sonntag - kein Post geplant');
}

return [{
  json: {
    ...todayConfig,
    date: now.toISOString().split('T')[0],
    timestamp: now.toISOString()
  }
}];

const postType = $('Wochentag ermitteln').item.json.post_type;
const allContent = $('Google Sheets - Content laden').all();

// Filter: passender post_type UND status = "ready"
const readyContent = allContent.filter(item => 
  item.json.post_type === postType && 
  item.json.status === 'ready'
);

if (readyContent.length === 0) {
  throw new Error(`Kein Content mit status "ready" für post_type "${postType}" gefunden!`);
}

// Zufällige Auswahl
const randomIndex = Math.floor(Math.random() * readyContent.length);
const selectedContent = readyContent[randomIndex];

return [selectedContent];

const postType = $('Wochentag ermitteln').item.json.post_type;
const allPrompts = $('Google Sheets - Prompts laden').all();

// Filter: passender post_type
const matchingPrompts = allPrompts.filter(item => 
  item.json.post_type === postType
);

if (matchingPrompts.length === 0) {
  throw new Error(`Keine Prompts für post_type "${postType}" gefunden!`);
}

// Zufällige Auswahl
const randomIndex = Math.floor(Math.random() * matchingPrompts.length);
const selectedPrompt = matchingPrompts[randomIndex];

return [selectedPrompt];

const businessInfo = $('Google Sheets - Business Info').item.json;
const weekdayInfo = $('Wochentag ermitteln').item.json;
const contentData = $('Content filtern & auswählen').item.json;
const promptData = $('Prompt filtern & auswählen').item.json;

const systemPrompt = `Du bist ein erfahrener Social Media Content Writer für den deutschen Markt. 

Deine Aufgabe:
- Schreibe Posts auf Deutsch
- Halte dich exakt an das angegebene Format und die Wortanzahl
- Verwende den angegebenen Tone of Voice
- Mache den Content spezifisch und konkret, nicht generisch
- Nutze kurze Sätze und Absätze für bessere Lesbarkeit
- Verwende Emojis sparsam und nur wenn sie passen
- Formatiere für LinkedIn (Zeilenumbrüche für Lesbarkeit)

Antworte NUR mit dem fertigen Post-Text. Keine Erklärungen, keine Einleitung.`;

const userMessage = `### Business Kontext:
- Firma: ${businessInfo.business_name}
- Branche: ${businessInfo.industry}
- Zielgruppe (ICP): ${businessInfo.icp_name}
- Pain Points der Zielgruppe: ${businessInfo.icp_pain_points}
- Gewünschte Ergebnisse: ${businessInfo.icp_desired_outcomes}
- Tone of Voice: ${businessInfo.tone_of_voice}
- Standard CTA: ${businessInfo.cta_default}

### Post-Typ: ${weekdayInfo.post_type}

### Content-Daten für diesen Post:
${JSON.stringify(contentData, null, 2)}

### Prompt/Anweisungen:
${promptData.prompt_text}

---

Schreibe jetzt den Post auf Deutsch. Nutze die Content-Daten um den Prompt zu füllen.`;

return [{
  json: {
    systemPrompt,
    userMessage,
    businessInfo,
    weekdayInfo,
    contentData,
    promptData
  }
}];

const response = $('HTTP - Google Drive Bilder Listen').item.json;
const files = response.files;
const previousData = $('Claude Antwort extrahieren').item.json;

if (!files || files.length === 0) {
  throw new Error('Keine Bilder im Google Drive Ordner gefunden!');
}

// Zufällige Auswahl
const randomIndex = Math.floor(Math.random() * files.length);
const selectedFile = files[randomIndex];

return [{
  json: {
    ...previousData,
    selectedImageId: selectedFile.id,
    selectedImageName: selectedFile.name
  }
}];

const previousData = $('Zufälliges Bild auswählen').item.json;
const binaryData = $input.item.binary;

const imagePrompt = `Erstelle ein fotorealistisches Bild im UGC-Stil (User Generated Content) für einen Social Media Post.

Das Bild soll die folgende Person zeigen (siehe Referenzbild) in diesem Kontext:
${previousData.contentData.image_context}

Anforderungen:
- Fotorealistisch, wie ein echtes Smartphone-Foto
- Natürliche Beleuchtung
- Die Person aus dem Referenzbild soll erkennbar sein (gleiches Gesicht, Haare, etc.)
- Authentischer UGC-Look (nicht zu perfekt, nicht zu gestellt)
- Passend für LinkedIn/Instagram
- Quadratisches Format (1:1)

Post-Kontext für das Bild:
${previousData.generated_text}`;

return [{
  json: {
    ...previousData,
    imagePrompt,
    imageType: 'ugc'
  },
  binary: binaryData
}];

const previousData = $('Claude Antwort extrahieren').item.json;

const imagePrompt = `Erstelle eine karikaturistische Illustration für einen Social Media Post.

Das Bild soll folgendes Thema visualisieren:
${previousData.contentData.image_context}

Anforderungen:
- Karikatur-Stil mit übertriebenen Proportionen
- Farbenfroh und auffällig
- Humorvoll aber professionell
- Kein Text im Bild
- Passend für LinkedIn/Instagram
- Quadratisches Format (1:1)

Post-Kontext für das Bild:
${previousData.generated_text}`;

return [{
  json: {
    ...previousData,
    imagePrompt,
    imageType: 'karikatur'
  }
}];

const mergeData = $('Merge - Pfade zusammenführen').item.json;
const blotatoResponse = $input.item.json;

// Extrahiere URLs aus Blotato-Antwort (Struktur muss ggf. angepasst werden)
const linkedinUrl = blotatoResponse.results?.linkedin?.url || '';
const instagramUrl = blotatoResponse.results?.instagram?.url || '';
const facebookUrl = blotatoResponse.results?.facebook?.url || '';
const xUrl = blotatoResponse.results?.twitter?.url || '';

return [{
  json: {
    date: mergeData.weekdayInfo.date,
    weekday: mergeData.weekdayInfo.weekday,
    post_type: mergeData.weekdayInfo.post_type,
    content_id: mergeData.contentData.id,
    prompt_id: mergeData.promptData.prompt_id,
    image_type: mergeData.weekdayInfo.image_type,
    platforms: 'linkedin, instagram, facebook, x',
    status: 'success',
    error_message: '',
    generated_text: mergeData.generated_text,
    linkedin_url: linkedinUrl,
    instagram_url: instagramUrl,
    facebook_url: facebookUrl,
    x_url: xUrl,
    contentData: mergeData.contentData
  }
}];

const error = $input.item.json;
const now = new Date();

// Versuche Daten aus vorherigen Nodes zu extrahieren (falls verfügbar)
let weekdayInfo = {};
let contentData = {};
let promptData = {};

try {
  weekdayInfo = $('Wochentag ermitteln')?.item?.json || {};
} catch (e) {}

try {
  contentData = $('Content filtern & auswählen')?.item?.json || {};
} catch (e) {}

try {
  promptData = $('Prompt filtern & auswählen')?.item?.json || {};
} catch (e) {}

return [{
  json: {
    date: now.toISOString().split('T')[0],
    weekday: weekdayInfo.weekday || 'unknown',
    post_type: weekdayInfo.post_type || 'unknown',
    content_id: contentData.id || 'unknown',
    prompt_id: promptData.prompt_id || 'unknown',
    image_type: weekdayInfo.image_type || 'unknown',
    platforms: '',
    status: 'error',
    error_message: error.message || JSON.stringify(error),
    generated_text: '',
    linkedin_url: '',
    instagram_url: '',
    facebook_url: '',
    x_url: ''
  }
}];
