// SM Post Text aus vorherigem Node holen
const smText = $('SM Post erstellen').first().json.content[0].text;

// Bild-URL aus Blotato Media Upload
const imageUrl = $input.first().json.url;

// Posts anhand der Markierungen aufteilen
let linkedin = '';
let instagram = '';
let xPost = '';

if (smText.includes('---LINKEDIN---')) {
  const parts = smText.split(/---(?:LINKEDIN|INSTAGRAM|X)---/);
  linkedin = (parts[1] || '').trim();
  instagram = (parts[2] || '').trim();
  xPost = (parts[3] || '').trim();
} else {
  const sections = smText.split(/(?=(?:##?\s*)?(?:LinkedIn|Instagram|X(?:\s|:|$)|Twitter))/i);
  for (const section of sections) {
    const cleaned = section.replace(/^##?\s*/gm, '').trim();
    if (/^LinkedIn/i.test(cleaned)) {
      linkedin = cleaned.replace(/^LinkedIn[:\s]*/i, '').trim();
    } else if (/^Instagram/i.test(cleaned)) {
      instagram = cleaned.replace(/^Instagram[:\s]*/i, '').trim();
    } else if (/^(?:X|Twitter)/i.test(cleaned)) {
      xPost = cleaned.replace(/^(?:X|Twitter)[:\s]*/i, '').trim();
    }
  }
}

if (!linkedin && !instagram && !xPost) {
  linkedin = smText;
  instagram = smText;
  xPost = smText;
}

// Flaches Array – alle 3 Posts als Items auf einem Output
return [
  { json: { platform: 'linkedin', text: linkedin, imageUrl } },
  { json: { platform: 'instagram', text: instagram, imageUrl } },
  { json: { platform: 'x', text: xPost, imageUrl } }
];
