// Get current day of week (1=Monday, 6=Saturday, 0=Sunday)
const today = new Date();
const dayOfWeek = today.getDay();

// Sunday is day 0, but we skip Sundays via cron
// Map: Mon=1->Type1, Tue=2->Type2, Wed=3->Type3, Thu=4->Type4, Fri=5->Type5, Sat=6->Type6
const postTypeMap = {
  1: 'Authority Builder',
  2: 'Client Win',
  3: 'Controversy',
  4: 'Mistake Post',
  5: 'Audit/Critique',
  6: 'Data Drop'
};

// Also include How-To as alternative for any day
const postType = postTypeMap[dayOfWeek] || 'How-To';

// Get week number for cycle tracking
const startOfYear = new Date(today.getFullYear(), 0, 1);
const weekNumber = Math.ceil((((today - startOfYear) / 86400000) + startOfYear.getDay() + 1) / 7);

// Find content row for this week's cycle (6 posts per cycle)
const cycleWeek = weekNumber % 52 || 52;
const dayIndex = dayOfWeek - 1; // 0-5 for Mon-Sat

return {
  dayOfWeek,
  postType,
  weekNumber,
  cycleWeek,
  dayIndex,
  timestamp: today.toISOString()
};

// Combine results from Merge node
const items = $input.all();

let postText = "";
let imageUrl = "";
let originalData = null;
let binaryData = null;

for (const item of items) {
  const json = item.json;

  // Check for Gemini response
  if (json.candidates?.[0]?.content?.parts?.[0]?.text) {
    postText = json.candidates[0].content.parts[0].text;
  }
  if (json.content?.parts?.[0]?.text) {
    postText = json.content.parts[0].text;
  }
  if (json.text && !postText) {
    postText = json.text;
  }

  // Check for image URL
  if (json.data?.[0]?.url) {
    imageUrl = json.data[0].url;
  }
  if (json.url && json.url.startsWith("http")) {
    imageUrl = json.url;
  }

  // Preserve binary data from image generation
  if (item.binary?.data) {
    binaryData = item.binary;
  }

  // Check for original data
  if (json.postType && json.filledPrompt) {
    originalData = json;
  }
  if (json.contentData) {
    originalData = json;
  }
}

// Clean up the post text
postText = (postText || "").trim();

if (postText && !postText.includes("kostenfreie Analyse")) {
  postText += "\n\nðŸ”— Buchen Sie eine kostenfreie Analyse";
}

// Return with binary passthrough
const result = {
  json: {
    postType: originalData?.postType || "Data Drop",
    postText,
    imageUrl,
    promptId: originalData?.promptId || 0,
    generatedAt: new Date().toISOString(),
    hasImage: !!(imageUrl || binaryData),
    contentData: originalData?.contentData || {}
  }
};

// Pass binary data through if exists
if (binaryData) {
  result.binary = binaryData;
}

return result;

