// ========================================
// DATE RANGE CALCULATION
// Calculates the last complete week (Mon-Sun)
// ========================================

const now = new Date();

// Find last Monday (start of last complete week)
const dayOfWeek = now.getDay();
const daysToLastMonday = dayOfWeek === 0 ? 6 : dayOfWeek - 1;
const daysToStartOfLastWeek = daysToLastMonday + 7;

const lastWeekStart = new Date(now);
lastWeekStart.setDate(now.getDate() - daysToStartOfLastWeek);
lastWeekStart.setHours(0, 0, 0, 0);

// Last Sunday (end of last complete week)
const lastWeekEnd = new Date(lastWeekStart);
lastWeekEnd.setDate(lastWeekStart.getDate() + 6);
lastWeekEnd.setHours(23, 59, 59, 999);

// Format dates for APIs
const formatDate = (d) => d.toISOString().split('T')[0];

const startDate = formatDate(lastWeekStart);
const endDate = formatDate(lastWeekEnd);

// Get workflow variables (set these in n8n workflow settings)
const gscSiteUrl = $env.GSC_SITE_URL || 'https://example.com/';
const ga4PropertyId = $env.GA4_PROPERTY_ID || '123456789';
const slackChannel = $env.SLACK_CHANNEL || '#marketing-reports';
const reportRecipient = $env.REPORT_RECIPIENT || 'team@example.com';
const enableSlack = $env.ENABLE_SLACK !== 'false';
const enableEmail = $env.ENABLE_EMAIL !== 'false';
const enableSheets = $env.ENABLE_SHEETS === 'true';

return {
  startDate,
  endDate,
  dateRange: `${startDate} bis ${endDate}`,
  gscSiteUrl,
  ga4PropertyId,
  slackChannel,
  reportRecipient,
  enableSlack,
  enableEmail,
  enableSheets,
  weekNumber: getWeekNumber(lastWeekStart)
};

function getWeekNumber(d) {
  const onejan = new Date(d.getFullYear(), 0, 1);
  return Math.ceil((((d - onejan) / 86400000) + onejan.getDay() + 1) / 7);
}

// ========================================
// FUNNEL KPI CALCULATION & REPORT GENERATION
// ========================================

// Get input data
const dateConfig = $('Calculate Date Range').first().json;
const gscData = $('GSC Search Analytics').first().json;
const ga4Data = $('GA4 Funnel Report').all();

// ========================================
// 1. PROCESS GSC DATA
// ========================================
let gscMetrics = {
  totalClicks: 0,
  totalImpressions: 0,
  totalCtr: 0,
  byDevice: {},
  topPages: []
};

if (gscData && gscData.rows) {
  // Aggregate totals
  gscData.rows.forEach(row => {
    gscMetrics.totalClicks += row.clicks || 0;
    gscMetrics.totalImpressions += row.impressions || 0;

    // By device
    const device = row.keys[0] || 'unknown';
    if (!gscMetrics.byDevice[device]) {
      gscMetrics.byDevice[device] = { clicks: 0, impressions: 0 };
    }
    gscMetrics.byDevice[device].clicks += row.clicks || 0;
    gscMetrics.byDevice[device].impressions += row.impressions || 0;
  });

  // Calculate CTRs
  gscMetrics.totalCtr = gscMetrics.totalImpressions > 0 
    ? (gscMetrics.totalClicks / gscMetrics.totalImpressions * 100).toFixed(2) 
    : 0;

  Object.keys(gscMetrics.byDevice).forEach(device => {
    const d = gscMetrics.byDevice[device];
    d.ctr = d.impressions > 0 ? (d.clicks / d.impressions * 100).toFixed(2) : 0;
  });

  // Top pages by clicks
  const pageMap = {};
  gscData.rows.forEach(row => {
    const page = row.keys[1] || 'unknown';
    if (!pageMap[page]) pageMap[page] = { clicks: 0, impressions: 0 };
    pageMap[page].clicks += row.clicks || 0;
    pageMap[page].impressions += row.impressions || 0;
  });
  gscMetrics.topPages = Object.entries(pageMap)
    .map(([page, data]) => ({ page, ...data, ctr: data.impressions > 0 ? (data.clicks / data.impressions * 100).toFixed(2) : 0 }))
    .sort((a, b) => b.clicks - a.clicks)
    .slice(0, 5);
}

// ========================================
// 2. PROCESS GA4 DATA
// ========================================
let ga4Metrics = {
  total: {
    sessions: 0,
    pageViews: 0,
    itemViews: 0,
    addToCarts: 0,
    checkouts: 0,
    purchases: 0,
    revenue: 0,
    adCost: 0,
    adClicks: 0
  },
  byDevice: {},
  byChannel: {},
  byLandingPage: {}
};

// Map GA4 metric names
const metricMapping = {
  'sessions': 'sessions',
  'screenPageViews': 'pageViews',
  'itemViews': 'itemViews',
  'addToCarts': 'addToCarts',
  'checkouts': 'checkouts',
  'purchases': 'purchases',
  'totalRevenue': 'revenue',
  'advertiserAdCost': 'adCost',
  'advertiserAdClicks': 'adClicks'
};

ga4Data.forEach(row => {
  const device = row.json.deviceCategory || 'unknown';
  const channel = row.json.sessionDefaultChannelGroup || 'unknown';
  const landingPage = row.json.landingPage || 'unknown';

  // Initialize device/channel buckets
  if (!ga4Metrics.byDevice[device]) {
    ga4Metrics.byDevice[device] = { sessions: 0, itemViews: 0, addToCarts: 0, checkouts: 0, purchases: 0, revenue: 0 };
  }
  if (!ga4Metrics.byChannel[channel]) {
    ga4Metrics.byChannel[channel] = { sessions: 0, itemViews: 0, addToCarts: 0, checkouts: 0, purchases: 0, revenue: 0 };
  }
  if (!ga4Metrics.byLandingPage[landingPage]) {
    ga4Metrics.byLandingPage[landingPage] = { sessions: 0, checkouts: 0, purchases: 0 };
  }

  // Aggregate metrics
  const sessions = parseFloat(row.json.sessions) || 0;
  const pageViews = parseFloat(row.json.screenPageViews) || 0;
  const itemViews = parseFloat(row.json.itemViews || row.json['ecommerce:itemViews']) || 0;
  const addToCarts = parseFloat(row.json.addToCarts) || 0;
  const checkouts = parseFloat(row.json.checkouts) || 0;
  const purchases = parseFloat(row.json.purchases) || 0;
  const revenue = parseFloat(row.json.totalRevenue) || 0;
  const adCost = parseFloat(row.json.advertiserAdCost) || 0;
  const adClicks = parseFloat(row.json.advertiserAdClicks) || 0;

  // Add to totals
  ga4Metrics.total.sessions += sessions;
  ga4Metrics.total.pageViews += pageViews;
  ga4Metrics.total.itemViews += itemViews;
  ga4Metrics.total.addToCarts += addToCarts;
  ga4Metrics.total.checkouts += checkouts;
  ga4Metrics.total.purchases += purchases;
  ga4Metrics.total.revenue += revenue;
  ga4Metrics.total.adCost += adCost;
  ga4Metrics.total.adClicks += adClicks;

  // By device
  ga4Metrics.byDevice[device].sessions += sessions;
  ga4Metrics.byDevice[device].itemViews += itemViews;
  ga4Metrics.byDevice[device].addToCarts += addToCarts;
  ga4Metrics.byDevice[device].checkouts += checkouts;
  ga4Metrics.byDevice[device].purchases += purchases;
  ga4Metrics.byDevice[device].revenue += revenue;

  // By channel
  ga4Metrics.byChannel[channel].sessions += sessions;
  ga4Metrics.byChannel[channel].itemViews += itemViews;
  ga4Metrics.byChannel[channel].addToCarts += addToCarts;
  ga4Metrics.byChannel[channel].checkouts += checkouts;
  ga4Metrics.byChannel[channel].purchases += purchases;
  ga4Metrics.byChannel[channel].revenue += revenue;

  // By landing page
  ga4Metrics.byLandingPage[landingPage].sessions += sessions;
  ga4Metrics.byLandingPage[landingPage].checkouts += checkouts;
  ga4Metrics.byLandingPage[landingPage].purchases += purchases;
});

// ========================================
// 3. CALCULATE KPIs
// ========================================
const t = ga4Metrics.total;

// CPC (Cost per Click) - nur wenn Kosten vorhanden
const cpc = t.adCost > 0 && t.adClicks > 0 
  ? (t.adCost / t.adClicks).toFixed(2) 
  : null;
const cpcAvailable = cpc !== null;

// Landing Page Conversion Rate (LP-CR)
// Definition: Checkouts / Sessions (wie viele Sessions f√ºhren zum Checkout)
const lpCr = t.sessions > 0 
  ? (t.checkouts / t.sessions * 100).toFixed(2) 
  : 0;

// Checkout Conversion Rate (Checkout-CR)
// Definition: Purchases / Checkouts
const checkoutCr = t.checkouts > 0 
  ? (t.purchases / t.checkouts * 100).toFixed(2) 
  : 0;

// Product Page to Checkout Rate
const productToCheckoutRate = t.itemViews > 0 
  ? (t.checkouts / t.itemViews * 100).toFixed(2) 
  : 0;

// Abbruchraten (Drop-off Rates)
// Produktseiten-Abbruch: 1 - (Checkouts / ItemViews)
const productDropOff = t.itemViews > 0 
  ? ((1 - t.checkouts / t.itemViews) * 100).toFixed(2) 
  : 0;

// Checkout-Abbruch: 1 - (Purchases / Checkouts)
const checkoutDropOff = t.checkouts > 0 
  ? ((1 - t.purchases / t.checkouts) * 100).toFixed(2) 
  : 0;

// Add-to-Cart Rate (wenn vorhanden)
const addToCartRate = t.itemViews > 0 && t.addToCarts > 0
  ? (t.addToCarts / t.itemViews * 100).toFixed(2)
  : null;

// ========================================
// 4. DECISION LOGIC - Where is the leak?
// ========================================
const issues = [];
const recommendations = [];

// CTR Analysis (GSC)
const ctrValue = parseFloat(gscMetrics.totalCtr);
if (ctrValue < 2.0) {
  issues.push({ area: 'CTR', severity: 'high', value: ctrValue + '%' });
  recommendations.push('üéØ **CTR niedrig (<2%)**: Meta-Titles und Descriptions optimieren, strukturierte Daten pr√ºfen, Featured Snippets anstreben');
} else if (ctrValue < 4.0) {
  issues.push({ area: 'CTR', severity: 'medium', value: ctrValue + '%' });
  recommendations.push('üìä **CTR verbesserungsw√ºrdig**: A/B-Tests f√ºr Titles durchf√ºhren, emotionale Trigger einbauen');
}

// LP-CR Analysis
const lpCrValue = parseFloat(lpCr);
if (lpCrValue < 2.0) {
  issues.push({ area: 'LP-CR', severity: 'high', value: lpCr + '%' });
  recommendations.push('üö® **LP-CR kritisch niedrig (<2%)**: Landingpage komplett √ºberarbeiten - Ladezeit, Above-the-fold, CTA, Message Match pr√ºfen');
} else if (lpCrValue < 5.0) {
  issues.push({ area: 'LP-CR', severity: 'medium', value: lpCr + '%' });
  recommendations.push('üìÑ **LP-CR verbesserungsw√ºrdig**: Heatmaps analysieren, Trust-Elemente hinzuf√ºgen, CTA-Positionen testen');
}

// Product Page Drop-off
const productDropOffValue = parseFloat(productDropOff);
if (productDropOffValue > 90) {
  issues.push({ area: 'Produktseite', severity: 'high', value: productDropOff + '% Abbruch' });
  recommendations.push('üõçÔ∏è **Produktseiten-Abbruch sehr hoch (>90%)**: Offer/Preis √ºberpr√ºfen, Produktbilder verbessern, Versandkosten fr√ºher zeigen, Trust-Badges hinzuf√ºgen');
} else if (productDropOffValue > 80) {
  issues.push({ area: 'Produktseite', severity: 'medium', value: productDropOff + '% Abbruch' });
  recommendations.push('üì¶ **Produktseiten-Abbruch hoch**: Social Proof verst√§rken, Produktbeschreibungen optimieren, Zoom/Video hinzuf√ºgen');
}

// Checkout Drop-off
const checkoutDropOffValue = parseFloat(checkoutDropOff);
if (checkoutDropOffValue > 80) {
  issues.push({ area: 'Checkout', severity: 'high', value: checkoutDropOff + '% Abbruch' });
  recommendations.push('üí≥ **Checkout-Abbruch kritisch (>80%)**: Checkout-Prozess vereinfachen, Zahlungsmethoden erweitern, Gast-Checkout erm√∂glichen, versteckte Kosten eliminieren');
} else if (checkoutDropOffValue > 60) {
  issues.push({ area: 'Checkout', severity: 'medium', value: checkoutDropOff + '% Abbruch' });
  recommendations.push('üõí **Checkout-Abbruch hoch**: Progress-Indicator hinzuf√ºgen, Formularfelder reduzieren, Abandoned Cart Emails einrichten');
}

// Determine biggest leak
let biggestLeak = 'Keine kritischen Probleme identifiziert';
let optimizationFocus = 'Allgemeine Optimierung';

const highIssues = issues.filter(i => i.severity === 'high');
if (highIssues.length > 0) {
  const leakMap = {
    'CTR': { leak: 'Suchmaschinen-Sichtbarkeit (CTR)', focus: 'Creatives/Meta-Daten' },
    'LP-CR': { leak: 'Landingpage-Conversion', focus: 'Landingpage' },
    'Produktseite': { leak: 'Produktseiten-Performance', focus: 'Offer/Produktseite' },
    'Checkout': { leak: 'Checkout-Prozess', focus: 'Checkout' }
  };
  const firstHighIssue = highIssues[0];
  biggestLeak = leakMap[firstHighIssue.area]?.leak || firstHighIssue.area;
  optimizationFocus = leakMap[firstHighIssue.area]?.focus || 'Allgemein';
}

// Top 3 Recommendations
const top3Recommendations = recommendations.slice(0, 3);
if (top3Recommendations.length === 0) {
  top3Recommendations.push('‚úÖ Alle KPIs im gr√ºnen Bereich - Fokus auf Skalierung und A/B-Tests f√ºr inkrementelle Verbesserungen');
}

// ========================================
// 5. BUILD DEVICE SPLIT TABLE
// ========================================
let deviceTable = '| Device | Sessions | Checkouts | Purchases | LP-CR | Checkout-CR |\n';
deviceTable += '|--------|----------|-----------|-----------|-------|-------------|\n';

Object.entries(ga4Metrics.byDevice).forEach(([device, data]) => {
  const deviceLpCr = data.sessions > 0 ? (data.checkouts / data.sessions * 100).toFixed(1) : '0.0';
  const deviceCheckoutCr = data.checkouts > 0 ? (data.purchases / data.checkouts * 100).toFixed(1) : '0.0';
  deviceTable += `| ${device} | ${Math.round(data.sessions)} | ${Math.round(data.checkouts)} | ${Math.round(data.purchases)} | ${deviceLpCr}% | ${deviceCheckoutCr}% |\n`;
});

// ========================================
// 6. BUILD TRAFFIC SPLIT TABLE
// ========================================
let trafficTable = '| Channel | Sessions | Checkouts | Purchases | LP-CR | Checkout-CR |\n';
trafficTable += '|---------|----------|-----------|-----------|-------|-------------|\n';

const sortedChannels = Object.entries(ga4Metrics.byChannel)
  .sort((a, b) => b[1].sessions - a[1].sessions)
  .slice(0, 10);

sortedChannels.forEach(([channel, data]) => {
  const channelLpCr = data.sessions > 0 ? (data.checkouts / data.sessions * 100).toFixed(1) : '0.0';
  const channelCheckoutCr = data.checkouts > 0 ? (data.purchases / data.checkouts * 100).toFixed(1) : '0.0';
  trafficTable += `| ${channel} | ${Math.round(data.sessions)} | ${Math.round(data.checkouts)} | ${Math.round(data.purchases)} | ${channelLpCr}% | ${channelCheckoutCr}% |\n`;
});

// ========================================
// 7. TOP LANDING PAGES BY WORST CR
// ========================================
let worstLpTable = '';
const landingPages = Object.entries(ga4Metrics.byLandingPage)
  .filter(([_, data]) => data.sessions >= 10) // Mindestens 10 Sessions
  .map(([page, data]) => ({
    page: page.length > 50 ? page.substring(0, 47) + '...' : page,
    sessions: data.sessions,
    checkouts: data.checkouts,
    lpCr: data.sessions > 0 ? (data.checkouts / data.sessions * 100) : 0
  }))
  .sort((a, b) => a.lpCr - b.lpCr)
  .slice(0, 5);

if (landingPages.length > 0) {
  worstLpTable = '| Landingpage | Sessions | Checkouts | LP-CR |\n';
  worstLpTable += '|-------------|----------|-----------|-------|\n';
  landingPages.forEach(lp => {
    worstLpTable += `| ${lp.page} | ${Math.round(lp.sessions)} | ${Math.round(lp.checkouts)} | ${lp.lpCr.toFixed(1)}% |\n`;
  });
}

// ========================================
// 8. GENERATE MARKDOWN REPORT
// ========================================
const report = `# üìä Funnel & Conversion Report

**Zeitraum:** ${dateConfig.dateRange} (KW ${dateConfig.weekNumber})
**Erstellt:** ${new Date().toLocaleDateString('de-DE', { weekday: 'long', year: 'numeric', month: 'long', day: 'numeric' })}

---

## üéØ KPI-√úbersicht

| Metrik | Wert | Status |
|--------|------|--------|
| **CTR (GSC)** | ${gscMetrics.totalCtr}% | ${ctrValue >= 4 ? 'üü¢' : ctrValue >= 2 ? 'üü°' : 'üî¥'} |
| **CPC** | ${cpcAvailable ? cpc + ' ‚Ç¨' : '‚ö†Ô∏è nicht verf√ºgbar'} | ${cpcAvailable ? 'üìä' : '‚ùì'} |
| **LP-CR** | ${lpCr}% | ${lpCrValue >= 5 ? 'üü¢' : lpCrValue >= 2 ? 'üü°' : 'üî¥'} |
| **Checkout-CR** | ${checkoutCr}% | ${parseFloat(checkoutCr) >= 40 ? 'üü¢' : parseFloat(checkoutCr) >= 20 ? 'üü°' : 'üî¥'} |

${!cpcAvailable ? '> ‚ö†Ô∏è **CPC nicht verf√ºgbar:** Keine Kosten-/Klickdaten in GA4. F√ºr CPC-Tracking Google Ads mit GA4 verkn√ºpfen.\n' : ''}

---

## üìâ Abbruchraten (Drop-off)

| Funnel-Schritt | Abbruchrate | Bewertung |
|----------------|-------------|------------|
| **Produktseite ‚Üí Checkout** | ${productDropOff}% | ${productDropOffValue <= 80 ? 'üü¢' : productDropOffValue <= 90 ? 'üü°' : 'üî¥'} |
| **Checkout ‚Üí Kauf** | ${checkoutDropOff}% | ${checkoutDropOffValue <= 60 ? 'üü¢' : checkoutDropOffValue <= 80 ? 'üü°' : 'üî¥'} |
${addToCartRate ? `| **Add-to-Cart Rate** | ${addToCartRate}% | üìä |\n` : ''}

---

## üì± Device-Split

${deviceTable}

---

## üö¶ Traffic-Split (Top 10 Channels)

${trafficTable}

---

## üö® Biggest Leak & Empfehlung

**Gr√∂√ütes Problem:** ${biggestLeak}

**Empfohlener Optimierungsfokus:** ${optimizationFocus}

### Next Actions:

${top3Recommendations.map((r, i) => `${i + 1}. ${r}`).join('\n\n')}

---

${worstLpTable ? `## üìÑ Top 5 Landingpages mit schlechtester CR (min. 10 Sessions)\n\n${worstLpTable}\n\n---\n` : ''}

## üìà Rohdaten-Zusammenfassung

**GSC:**
- Impressions: ${gscMetrics.totalImpressions.toLocaleString('de-DE')}
- Clicks: ${gscMetrics.totalClicks.toLocaleString('de-DE')}

**GA4:**
- Sessions: ${Math.round(t.sessions).toLocaleString('de-DE')}
- Page Views: ${Math.round(t.pageViews).toLocaleString('de-DE')}
- Item Views: ${Math.round(t.itemViews).toLocaleString('de-DE')}
- Checkouts: ${Math.round(t.checkouts).toLocaleString('de-DE')}
- Purchases: ${Math.round(t.purchases).toLocaleString('de-DE')}
- Revenue: ${t.revenue.toLocaleString('de-DE', { style: 'currency', currency: 'EUR' })}

---

*Dieser Report wurde automatisch generiert. Bei Fragen wenden Sie sich an das Marketing-Team.*
`;

// ========================================
// 9. PREPARE OUTPUT FOR DOWNSTREAM NODES
// ========================================
return {
  report,
  reportPlainText: report.replace(/[#*|`]/g, '').replace(/---/g, '\n'),
  kpis: {
    ctr: gscMetrics.totalCtr,
    cpc,
    lpCr,
    checkoutCr,
    productDropOff,
    checkoutDropOff
  },
  raw: {
    gsc: gscMetrics,
    ga4: ga4Metrics.total
  },
  config: dateConfig,
  biggestLeak,
  optimizationFocus,
  recommendations: top3Recommendations,
  sheetsRow: [
    dateConfig.startDate,
    dateConfig.endDate,
    gscMetrics.totalCtr,
    cpc || '',
    lpCr,
    checkoutCr,
    productDropOff,
    checkoutDropOff,
    Math.round(t.sessions),
    Math.round(t.purchases),
    t.revenue.toFixed(2),
    biggestLeak,
    optimizationFocus
  ]
};
